import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {clicks : 0 };
  }
  addClick() {
    this.setState({clicks: this.state.clicks +1})
  }

  handleClick() {
    this.setState((prevState) => ({
         clicks: prevState.clicks + 1
      }));
  }

  render() {
    return (
      <div>
        <h1>Article Title - {this.props.title}</h1>
        <img src='https://placekitten.com/g/200/300' alt="meaningful cat"></img>
        <p><button onClick={this.setState.addClick}>Clicks</button></p>        
      </div>

    );
  }
}

export default App;
